mapper.select:
SELECT * FROM board 

ORDER BY ${orderby} ${direct}			
LIMIT ${strIdx},${pageRow}

mapper.update:
UPDATE board SET
	title=#{title},content=#{content} 

		, filename_org=#{filename_org},filename_real=#{filename_real} 

WHERE no=#{no}

UPDATE board SET
	 filename_org='',filename_real=''
WHERE no=#{no}
